import React from 'react';
import './coins.css';

const coins = ({
  name,
  price,
  symbol,
  marketcap,
  volume,
  image,
  priceChange
}) => {
  return (
    <div className='coins-container'>
      <div className='coins-row'>
        <div className='coins'>
          <img src={image} alt='crypto' />
          <h1>{name}</h1>
          <p className='coins-symbol'>{symbol}</p>
        </div>
        <div className='coins-data'>
          <p className='coins-price'>${price}</p>
          <p className='coins-volume'>${volume.toLocaleString()}</p>

          {priceChange < 0 ? (
            <p className='coins-percent red'>{priceChange.toFixed(2)}%</p>
          ) : (
            <p className='coins-percent green'>{priceChange.toFixed(2)}%</p>
          )}

          <p className='coins-marketcap'>
            Mkt Cap: ${marketcap.toLocaleString()}
          </p>
        </div>
      </div>
    </div>
  );
};

export default coins;
