import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Moneda from './Moneda';

function App() {
  const [monedas, setMonedas] = useState([]);
  const [search,setBusqueda] = useState('');

  useEffect(() => {
    axios
      .get(
        'https://api.coingecko.com/api/v3/monedas/markets?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false'
      )
      .then(res => {
        setMonedas(res.data);
        console.log(res.data);
      })
      .catch(error => console.log(error));
  }, []);

  const handleChange = e => {
  setBusqueda(e.target.value);
  };

  const filteredmonedas = monedas.filter(coin =>
    coin.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className='coin-app'>
      <div className='coin-search'>
        <h1 className='coin-text'>Search a currency</h1>
        <form>
          <input
            className='coin-input'
            type='text'
            onChange={handleChange}
            placeholder='Search'
          />
        </form>
      </div>
      {filteredmonedas.map(coin => {
        return (
          <Coin
            key={coin.id}
            nombre={coin.name}
            precio={coin.current_price}
            simbolo={coin.symbol}
            marketcap={coin.total_volume}
            volume={coin.market_cap}
            image={coin.image}
            priceChange={coin.price_change_percentage_24h}
          />
        );
      })}
    </div>
  );
}

export default App;
