import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Monedas from './Monedas';

function App() {
  const [monedas, setMonedas] = useState([]);
  const [busqueda,setBusqueda] = useState('');

  useEffect(() => {
    axios
      .get(
        'https://api.monedagecko.com/api/v3/monedas/markets?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false'
      )
      .then(res => {
        setMonedas(res.data);
        console.log(res.data);
      })
      .catch(error => console.log(error));
  }, []);

  const handleChange = e => {
  setBusqueda(e.target.value);
  };

  const filtroMonedas = monedas.filter(monedas =>
    monedas.name.toLowerCase().includes(busqueda.toLowerCase())
  );

  return (
    <div className='monedas-app'>
      <div className='monedas-busqueda'>
        <h1 className='monedas-text'>busqueda a currency</h1>
        <form>
          <input
            className='monedas-input'
            type='text'
            onChange={handleChange}
            placeholder='busqueda'
          />
        </form>
      </div>
      {filtroMonedas.map(monedas => {
        return (
          <Moneda
            key={monedas.id}
            name={monedas.name}
            price={monedas.current_price}
            symbol={monedas.symbol}
            marketcap={monedas.total_volume}
            volume={monedas.market_cap}
            image={monedas.image}
            priceChange={monedas.price_change_percentage_24h}
          />
        );
      })}
    </div>
  );
}

export default App;
