import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Moneda from './Moneda';

function App() {
  const [monedas, setMonedas] = useState([]);
  const [busqueda,setBusqueda] = useState('');

  useEffect(() => {
    axios
      .get(
        'https://api.monedagecko.com/api/v3/monedas/markets?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false'
      )
      .then(res => {
        setMonedas(res.data);
        console.log(res.data);
      })
      .catch(error => console.log(error));
  }, []);

  const handleChange = e => {
  setBusqueda(e.target.value);
  };

  const filtroMonedas = monedas.filter(moneda =>
    moneda.name.toLowerCase().includes(busqueda.toLowerCase())
  );

  return (
    <div className='moneda-app'>
      <div className='moneda-busqueda'>
        <h1 className='moneda-text'>busqueda a currency</h1>
        <form>
          <input
            className='moneda-input'
            type='text'
            onChange={handleChange}
            placeholder='busqueda'
          />
        </form>
      </div>
      {filtroMonedas.map(moneda => {
        return (
          <moneda
            key={moneda.id}
            nombre={moneda.name}
            precio={moneda.current_price}
            simbolo={moneda.symbol}
            marketcap={moneda.total_volume}
            volume={moneda.market_cap}
            image={moneda.image}
            priceChange={moneda.price_change_percentage_24h}
          />
        );
      })}
    </div>
  );
}

export default App;
